warehouse, instructions = data[0].splitlines(), "".join([row for row in data[1].splitlines()])

walls = set()
objects = set()
for r, row in enumerate(warehouse):
    for c, char in enumerate(row):
        if char == "@":
            robotPosition = (r, c)
        elif char == "#":
            walls.add((r, c))
        elif char == "O":
            objects.add((r, c))
#print(walls, objects)

def isEmpty(r, c):
    return (r, c) not in walls and (r, c) not in objects

def canPushObject(nr, nc, dir):
    if dir == (-1, 0): # up
        for r in range(nr, 0, -1):
            if (r, nc) in walls:
                return False
            if isEmpty(r, nc):
                return True
    elif dir == (1, 0): # down
        for r in range(nr, len(warehouse)-1, 1):
            if (r, nc) in walls:
                return False
            if isEmpty(r, nc):
                return True
    elif dir == (0, 1): # right
        for c in range(nc, len(warehouse[0])-1, 1):
            if (nr, c) in walls:
                return False
            if isEmpty(nr, c):
                return True
    else: # left
        for c in range(nc, 0, -1):
            if (nr, c) in walls:
                return False
            if isEmpty(nr, c):
                return True
    return False

def getNextEmptySpace(nr, nc, dir):
    if dir == (-1, 0): # up
        for r in range(nr, 0, -1):
            if isEmpty(r, nc):
                return (r, nc)
    elif dir == (1, 0): # down
        for r in range(nr, len(warehouse)-1, 1):
            if isEmpty(r, nc):
                return (r, nc)
    elif dir == (0, 1): # right
        for c in range(nc, len(warehouse[0])-1, 1):
            if isEmpty(nr, c):
                return (nr, c)
    else: # left
        for c in range(nc, 0, -1):
            if isEmpty(nr, c):
                return (nr, c)

directions = {"<":(0, -1), ">": (0, 1), "^": (-1, 0), "v": (1, 0)}
for instruction in instructions:

    # get next location
    direction = directions[instruction]
    nextLocation = (robotPosition[0]+direction[0], robotPosition[1]+direction[1])

    # we are heading to a wall, skip this turn
    if nextLocation in walls:
        continue

    # we are trying to push an object
    if nextLocation in objects:
        if canPushObject(nextLocation[0], nextLocation[1], direction): # if we can push an object
            nextEmptyLocation = getNextEmptySpace(nextLocation[0], nextLocation[1], direction)
            objects.add(nextEmptyLocation) # move the object in front of robot to empty space
            objects.remove(nextLocation)
            robotPosition = nextLocation # move the robot
    else:
        robotPosition = nextLocation

total = 0
for r, c in objects:
    total += r*100 + c
print(total)